var GLPI=GLPI||{};GLPI.Forms=GLPI.Forms||{};GLPI.Forms.FaIconSelector=class{constructor(e){this.selectElement=e}init(){const e=this.fetchAvailableIcons();$(this.selectElement).select2({data:e,templateResult:this.renderIcon,templateSelection:this.renderIcon})}fetchAvailableIcons(){var e=[];for(let t=0;t<document.styleSheets.length;t++){const s=document.styleSheets[t].cssRules;for(let t=0;t<s.length;t++){const n=s[t];if(n.constructor.name!=="CSSStyleRule"){continue}const c=n.selectorText.split(",");for(let t=0;t<c.length;t++){const s=c[t].trim().match(/^\.(fa-[a-z-]+)::before$/);if(s!==null){const t=s[1];const n={id:t,text:t};if(!e.includes(n)){e.push(n)}}}}}return e}renderIcon(e){const t="'Font Awesome 6 Free', 'Font Awesome 6 Brands'";const s=document.createElement("span");s.innerHTML=`<i class="fa-lg fa-fw fa ${_.escape(e.id)}" style="font-family:${t};"></i> ${_.escape(e.id)}`;return s}};