export class GlpiFormItemDropdownAdvancedConfig{static instance=null;constructor(){if(GlpiFormItemDropdownAdvancedConfig.instance!==null){return GlpiFormItemDropdownAdvancedConfig.instance}this.registerEventListeners();GlpiFormItemDropdownAdvancedConfig.instance=this}findContainer(t){const o=t.find(`[data-glpi-form-editor-item-dropdown-advanced-configuration]`);return o.length>0?o:null}registerEventListeners(){$(document).on("glpi-form-editor-question-sub-type-changed",(t,o,i)=>{const e=this.findContainer(o);if(!e){return}this.updateFieldsVisibility(e,i);this.updateRootItemsDropdown(o,i)})}updateFieldsVisibility(t,o){const i=t.find("[data-glpi-form-editor-item-dropdown-advanced-configuration-visible-for-itemtype]");i.each((t,i)=>{const e=$(i).closest(".form-field");const n=$(i).attr("data-glpi-form-editor-item-dropdown-advanced-configuration-visible-for-itemtype");const a=n==o;e.toggleClass("d-none",!a)})}updateRootItemsDropdown(t,o){const i=t.find('[name="extra_data[root_items_id]"], [data-glpi-form-editor-original-name="extra_data[root_items_id]"]');const e=i.parent();e.children().attr("data-to-remove","true");e.load(`${CFG_GLPI.root_doc}/ajax/dropdownAllItems.php`,{idtable:o,width:"100%",name:i.data("glpi-form-editor-original-name")||i.attr("name"),"aria-label":i.attr("aria-label")},()=>{const o=t.find('[name="extra_data[root_items_id]"], [data-glpi-form-editor-original-name="extra_data[root_items_id]"]');t.find(`[for="${CSS.escape(i.attr("id"))}"]`).attr("for",o.attr("id"));e.find("[data-to-remove]").remove()})}}