import{ProgressIndicator}from"/js/modules/ProgressIndicator.js";export function init_database(){const e=document.getElementById("glpi_install_messages_container");const t=document.getElementById("glpi_install_success");const s=document.getElementById("glpi_install_back");const n=new Request(`${CFG_GLPI.root_doc}/Install/InitDatabase`,{method:"POST",headers:{Accept:"application/json","Content-Type":"application/x-www-form-urlencoded;","X-Requested-With":"XMLHttpRequest","X-Glpi-Csrf-Token":getAjaxCsrfToken()}});const o=new ProgressIndicator({container:e,request:n,success_callback:()=>{t.querySelector('button[type="submit"]').removeAttribute("disabled");t.setAttribute("class","d-inline")},error_callback:()=>{s.querySelector('button[type="submit"]').removeAttribute("disabled");s.setAttribute("class","d-inline")}});o.start()}export async function update_database(){const e=document.getElementById("glpi_update_messages_container");const t=document.getElementById("glpi_update_success");const s=new Request(`${CFG_GLPI.root_doc}/Install/UpdateDatabase`,{method:"POST",headers:{Accept:"application/json","Content-Type":"application/x-www-form-urlencoded;","X-Requested-With":"XMLHttpRequest","X-Glpi-Csrf-Token":getAjaxCsrfToken()}});const n=new ProgressIndicator({container:e,request:s,success_callback:()=>{t.querySelector('button[type="submit"]').removeAttribute("disabled");t.setAttribute("class","d-inline")}});n.start()}