class Knowbase{constructor(){this.#e()}#e(){$(document).on("click",".restore_rev",()=>window.confirm(__("Do you want to restore the selected revision?")));$(document).on("click",".show_rev",e=>{e.preventDefault();const t=$(e.currentTarget);$.ajax({url:`${CFG_GLPI.root_doc}/ajax/getKbRevision.php`,method:"GET",cache:false,data:{revid:t.data("revid")}}).done(e=>{glpi_html_dialog({title:__("Show revision %d").replace("%d",t.data("rev")),body:`\n                            <div>\n                                <h2>${__("Subject")}</h2>\n                                <div>${_.escape(e.name)}</div>\n                                <h2>${__("Content")}</h2>\n                                <div>${e.answer}</div>\n                            </div>\n                        `})}).fail(()=>{glpi_alert({title:__("Contact your GLPI admin!"),message:__("Unable to load revision!")})})});$(document).on("click",".compare",e=>{e.preventDefault();const t=$('[name="oldid"]:checked').val();const i=$('[name="diff"]:checked').val();const n=$(e.currentTarget).data("kbitem_id");this.#t(n,t,i)});$('[name="diff"]:gt(0)').css("visibility","hidden");$('[name="oldid"]').on("click",e=>{const t=$(e.target).index('[name="oldid"]');const i=$('[name="diff"]:checked').index('[name="diff"]');if(i>=t){$(`[name="diff"]:eq(${t-1})`).prop("checked",true)}$(`[name="diff"]:gt(${t}), [name="diff"]:eq(${t})`).css("visibility","hidden");$(`[name="diff"]:lt(${t})`).css("visibility","visible")})}#t(e,t,i){const n=import("/lib/jquery-prettytextdiff.js");const a=$.ajax({url:`${CFG_GLPI.root_doc}/ajax/compareKbRevisions.php`,method:"post",cache:false,data:{oldid:t,diffid:i,kbid:e}});Promise.all([n,a]).then(([,n])=>{if(i===0){i=__("current")}glpi_html_dialog({title:__("Compare revisions %1$d and %2$d").replace("%1$d",t).replace("%2$d",i),body:`\n                    <div id="compare_view_${e}">\n                        <table class="table">\n                            <tr>\n                                <th></th>\n                                <th>${__("Original")}</th>\n                                <th>${__("Changed")}</th>\n                                <th>${__("Differences")}</th>\n                            </tr>\n                            <tr>\n                                <th>${__("Subject")}</th>\n                                <td class="original">${_.escape(n["old"]["name"])}</td>\n                                <td class="changed">${_.escape(n["diff"]["name"])}</td>\n                                <td class="diff"></td>\n                            </tr>\n                            <tr>\n                                <th>${__("Content")}</th>\n                                <td class="original">${n["old"]["answer"]}</td>\n                                <td class="changed">${n["diff"]["answer"]}</td>\n                                <td class="diff"></td>\n                            </tr>\n                        </table>\n                    </div>\n                `});$(`#compare_view_${e} tr`).prettyTextDiff()},()=>{glpi_alert({title:__("Contact your GLPI admin!"),message:__("Unable to load diff!")})})}}export default new Knowbase;